import React from 'react';
import { makeStyles } from '@material-ui/core/styles'
import { Grid, Card, CardContent, CardActions, Paper, Button, Typography } from '@material-ui/core/';
import {pink, blue} from '@material-ui/core/colors/';
import AdbIcon from '@material-ui/icons/Adb';
import '../mousePosition.js'
import useMousePosition from '../mousePosition.js';

function hexToRgb(hex) {
    var result = /^#?([a-f\d]{2})([a-f\d]{2})([a-f\d]{2})$/i.exec(hex);
    return result ? {
      r: parseInt(result[1], 16),
      g: parseInt(result[2], 16),
      b: parseInt(result[3], 16)
    } : null;
  }
  function componentToHex(c) {
    var hex = c.toString(16);
    return hex.length == 1 ? "0" + hex : hex;
  }
  
  function rgbToHex(r, g, b) {
    return "#" + componentToHex(r) + componentToHex(g) + componentToHex(b);
  }


export default function APP_HOME() {
    const {x,y} = useMousePosition();
    let xAxis = (window.innerWidth / 2 - x) / 10; let yAxis = (window.innerHeight / 2 - y) / 10;

    console.log(x,xAxis ,"|",y, yAxis);

    const rot = document.querySelector("#root > div > main > div:nth-child(2) > div > div:nth-child(1) > div");
    rot.style.transform = `rotateY(${yAxis}deg) rotateX(${xAxis}deg)`

    const useStyles = makeStyles((theme) => ({
        root: {

        },
        item: {
            padding: `0 ${theme.spacing(2)}px`
        },
        itemCard: {
            backgroundColor: theme.palette.background.paper,
            color: theme.palette.getContrastText(theme.palette.background.paper),
            border: "0",
            display: "flex",
            position: "relative",
            fontSize: ".875rem",
            minWidth: "0",
            margin: `${theme.spacing(3)}px 0`,
            boxShadow: "0 1px 4px 0 rgba(0, 0, 0, 0.14)",
            borderRadius: "6px",
            flexDirection: "column",
            overflow: "visible",
            borderRadius: "8px"

        },
        contents: {
            color: "#fff",
            textAlign: "right",
            background: "transparent",
            boxShadow: "none",
            margin: `0 ${theme.spacing(2)}px`,
            padding: 0,
            position: "relative"

        },
        iconBox: {
            float: "left",
            position: "relative",
            padding: `${theme.spacing(2)}px`,
            marginTop: `-${theme.spacing(3)}px`,
            marginRight: `${theme.spacing(2)}px`,
            borderRadius: "3px",
            background: `linear-gradient(60deg, ${blue[400]}, ${blue[700]})`,
            boxShadow: `0 4px 20px 0 rgba(0, 0, 0,.14), 0 7px 10px -5px rgba(${hexToRgb(blue[100]).r}, ${hexToRgb(blue[100]).g}, ${hexToRgb(blue[100]).b},.4)`,
            borderRadius: "8.1px"

        },
        cardTitle: {
            color: "#fff",
            overflow: "unset",
            fontSize: "36px",
            textAlign: "center",
            marginBottom: "1px"
        },
        buttonLine: {
            marginTop: "91px",
            marginBottom: "48px",
            height: "2px",
            backgroundColor: "hsla(0, 0%, 40%, .3)"
        },
        buttons: {
            padding: "8px 16px",
            border: "none",
            marginTop: "-24px",
            borderRadius: "8px",
            fontSize: "12px",
            marginLeft: "24px",
            fontWeight: "400",
            marginRight: "24px",
            boxShadow: theme.myShadows.button.color("233, 30, 99"),
            background: `linear-gradient(60deg, ${pink[300]}, ${pink[500]})`,
            color: "white",
            "&:hover": {
                backgroundColor: pink[500],
                boxShadow: theme.myShadows.button.hoverColor("233,30,99")
            }
        },
        form: {
            width: "100%",
            height: "600px",
            background: `linear-gradient(60deg,${blue[50]}, ${blue[200]})`,
            borderRadius: "8px",
            boxShadow: `0 4px 20px 0 rgba(0, 0, 0,.14), 0 7px 10px -5px rgba(${hexToRgb(blue[100]).r}, ${hexToRgb(blue[100]).g}, ${hexToRgb(blue[100]).b},.4)`
            //boxShadow: theme.shadows[5]
        },
        actions: {

        }
    }));

    const classes = useStyles();
    return (
        <div>
            <Grid
                className={classes.root}
                container
                spacing={3}
                direction="row"
                justify="center"
                alignItems="center"
                alignContent="center"

            >
                <Grid
                    item
                    className={classes.item}
                    xs={12}
                    s={6}
                    md={6}
                    lg={3}>
                    <Card className={classes.itemCard}>

                        <CardContent className={classes.contents}>
                        <Paper className={classes.iconBox}>
                                <Typography variant="h5" className={classes.cardTitle}>Drive Log</Typography>
                            </Paper>
                            <div className={classes.buttonLine}>
                                <Button className={classes.buttons} variant= "contained">Add</Button>
                            </div>
                            <Paper className={classes.form}>

                            </Paper>
                        </CardContent>
                        <CardActions>

                        </CardActions>
                    </Card>
                </Grid>
                <Grid
                    item
                    className={classes.item}
                    xs={12}
                    s={6}
                    md={6}
                    lg={3}>
                    <Card className={classes.itemCard}>

                        <CardContent className={classes.contents}>
                            
                        </CardContent>
                        <CardActions>

                        </CardActions>
                    </Card>
                </Grid>
            </Grid>
        </div>
    );
};
