import { Box, Paper } from "@material-ui/core";
import Divider from "@material-ui/core/Divider";
import Drawer from "@material-ui/core/Drawer";
import List from "@material-ui/core/List";
import ListItem from "@material-ui/core/ListItem";
import ListItemIcon from "@material-ui/core/ListItemIcon";
import ListItemText from "@material-ui/core/ListItemText";
import { makeStyles } from "@material-ui/core/styles";
import BookIcon from "@material-ui/icons/Book";
import MailIcon from "@material-ui/icons/Mail";
import InboxIcon from "@material-ui/icons/MoveToInbox";
import clsx from "clsx";
import React from "react";
import theme from "../theme";

const drawerWidth = 240;

const useStyles = makeStyles((theme) => ({
  siteIcon: {
    padding: theme.spacing(1)
  },
  drawer: {
    width: drawerWidth,
    //height: "100%",
    flexShrink: 0,
    whiteSpace: "nowrap"
    //boxShadow: theme.myShadows.drawer
  },
  drawerPaper: {
    boxShadow: theme.myShadows.drawer
  },
  drawerOpen: {
    width: drawerWidth,
    transition: theme.transitions.create("width", {
      easing: "ease",
      duration: ".35s"
    })
  },
  drawerClose: {
    transition: theme.transitions.create("width", {
      easing: "ease",
      duration: ".35s"
    }),
    //overflowX: "hidden",
    width: theme.spacing(10.5),
  },
  drawerIcon: {
    paddingLeft: theme.spacing(1),
    paddingRight: theme.spacing(1)
  },
  drawerListItem: {
    color: "inherit",
    padding: "0",
    margin: "0",
    display: "block",
    position: "relative",
  },
  drawerLIText: {
    height: 'auto',
    margin: '0',
    display: 'block',
    opacity: '1',
    padding: "0 16px !important",
    position: 'relative',
    transform: 'translate3d(0px, 0, 0)',
    transition: 'transform 300ms ease 0s, opacity 300ms ease 0s',
    lineHeight: '30px',
    
  },
  drawerLIPaper: {
    backgroundColor: '#eee',
    width: "auto",
    margin: theme.spacing(1) + "px " + theme.spacing(1.5) + "px 0",
    display: "block",
    padding: theme.spacing(1) + "px " + theme.spacing(1.5) + "px ",
    position: "relative",
    transition: "all 300ms linear",
    fontWeight: "300",
    lineHeight: "1.5em",
    paddingLeft: theme.spacing(1),
    paddingRight: theme.spacing(1),
    borderRadius: "3px",
    "&:hover": {
      backgroundColor: theme.myPalette.action.hover
    }
  },
  drawerLIPaperClose: {
    paddingTop: theme.spacing(1),
    paddingBottom: theme.spacing(1),
    marginTop: theme.spacing(1),
    display: "flex",
    alignItems: "center",
    position: "relative",
    width: `calc(100% - ${theme.spacing(2.5)}px)`,
    transition: "all 300ms linear",
    "&:hover": {
      backgroundColor: theme.myPalette.action.hover
    }
  },
  divider: {
    position: "absolute",
    width: `calc(100% - ${theme.spacing(4)}px)`,
    right: theme.spacing(2)
  }
}));

export default function APP_DRAWER(props) {
  const classes = useStyles();
  const { open, change, stay } = props;
  const setToOpen = () => (!stay ? change(true) : {});
  const setToClose = () => (!stay ? change(false) : {});

  return (
    <Drawer
      variant="permanent"
      className={clsx(classes.drawer, {
        [classes.drawerOpen]: open,
        [classes.drawerClose]: !open
      })}
      classes={{
        paper: clsx(
          { [classes.drawerOpen]: open, [classes.drawerClose]: !open },
          classes.drawerPaper
        )
      }}
      onMouseEnter={setToOpen}
      onMouseLeave={setToClose}
    >
      <div className={classes.toolbar}>
        <List>
          <ListItem>
            <ListItemIcon className={classes.siteIcon}>
              <BookIcon></BookIcon>
            </ListItemIcon>
            <ListItemText>Log Tool</ListItemText>
          </ListItem>
        </List>
      </div>
      <Box>
        <Divider className={classes.divider} />
      </Box>

      <List>
        {["Inbox", "Starred", "Send email", "Drafts"].map((text, index) => (
          <ListItem
            key={text}
            disableGutters
            className={classes.drawerListItem}
          >
            <Paper elevation={0} className={classes.drawerLIPaper}>
              <ListItemIcon className={classes.drawerIcon}>
                {index % 2 === 0 ? <InboxIcon /> : <MailIcon />}
              </ListItemIcon>
              <ListItemText className={classes.drawerLIText} primary={text} />
            </Paper>
          </ListItem>
        ))}
      </List>
      <Box>
        <Divider className={classes.divider} />
      </Box>

      <List>
        {["All mail", "Trash", "Spam"].map((text, index) => (
          <ListItem button key={text}>
            <ListItemIcon className={classes.drawerIcon}>
              {index % 2 === 0 ? <InboxIcon /> : <MailIcon />}
            </ListItemIcon>
            <ListItemText primary={text} />
          </ListItem>
        ))}
      </List>
    </Drawer>
  );
}
